import React from "react";
import { useState } from "react";
import axios from "axios";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";
import "./UserLoginStyle.css";
import { Button} from "@mui/material";
import { useNavigate } from "react-router-dom";
import authServices from "../../services/AuthServices";
import ReCAPTCHA from "react-google-recaptcha";
import { green } from "@mui/material/colors";


const UserLogin = () => {
  const nav = useNavigate();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");


  function onChange(value) {
    console.log("Captcha value:", value);
  }
  const [style, setStyle] = useState({
    color:"red",
    display:"none",
  });

  const cardStyles = {
    container: {
      display: "flex",
      height: 100,
      width: 400,
      boxShadow: "0 0 3px 2px #cec7c759",
      alignItems: "center",
      padding: 20,
      borderRadius: 20,
      backgroundColor : green,
    }
  }

  return (
    <>
      <div className="flex-container">
        <div style={cardStyles.container}>
          <Box
            component="form"
            sx={{
              "& > :not(style)": { m: 1, width: "30ch" },
            }}
            noValidate
            autoComplete="off"
          >
            <TextField
              label="Email"
              type="email"
              onChange={(e) => {
                setEmail(e.target.value);
              }}
            />{" "} 
            <br />
            <TextField
              label="Password"
              type="password"
              variant="outlined"
              onChange={(e) => {
                setPassword(e.target.value);
              }}
            />
            <br />
            <span  style={style}>Invalid Email/Password</span>
            <Button variant="contained" color="primary" className="btn" onClick={()=>{
              authServices.login(email,password).then((res)=>{
                if(res == false){
                setStyle({
                  color:"red",
                  display:"block",
                })}else{
                  console.log(authServices.isLogged());
                  alert("login Successfully");
                  nav("/");
                  window.location.reload(); 
                }
              }).catch((err)=>{
                console.log(err);
              })
            }}>
              Login
            </Button>
            <ReCAPTCHA
              sitekey="6LeqLwwhAAAAAMKLV8GMYWR1RCVQpDo4qM3sBB1y"
              onChange={onChange}
            />
          </Box>
        </div>
      </div>
    </>
  );
};

export default UserLogin;
