import * as React from 'react';
import Box from '@mui/material/Box';
import Modal from '@mui/material/Modal';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import userService from '../../services/UserService';
import { useState } from 'react';
import { ToastContainer } from 'react-toastify';
import { toast } from 'react-toastify';

const AddNewEditModel = ({ modelHandle , setModelHandle , setChangeHandler }) => {

    const [courseName,setCourseName] =React.useState(""); 
    const [instructorName,setInstructorName] =React.useState(""); 
    const [link,setLink] =React.useState(""); 
    const [description,setDescription] =React.useState(""); 

    const style = {
        position: 'absolute',
        top: '50%',
        left: '50%',
        transform: 'translate(-50%, -50%)',
        width: "500px",
        height:"420px" ,
        bgcolor: 'background.paper',
        border: '2px solid #2d5b67',
        borderTop:"40px solid #2d5b67",
        pt: 2,
        px: 4,
        pb: 3,
      };

        const handleClose = () => {
          setModelHandle(false);
        };
      
    return ( 
      <>
      <div style={{}}>
      <ToastContainer autoClose={2000} />
      <Modal
        hideBackdrop
        open={modelHandle}
        onClose={handleClose}
        aria-labelledby="child-modal-title"
        aria-describedby="child-modal-description"
      >
        <Box sx={{ ...style}}>

        <h3>Edit Form</h3>
        <TextField
          id="standard-multiline-flexible"
          label="InstructorName"
          value={instructorName}
          onChange={(e)=>{
            setInstructorName(e.target.value);
          }}
          multiline
          maxRows={1}
          fullWidth
          variant="standard"
        />
        <br />
        <br />
        <TextField
          id="standard-multiline-flexible"
          label="CourseTitle"
          value={courseName}
          onChange={(e)=>{
            setCourseName(e.target.value);
          }}
          multiline
          maxRows={1}
          fullWidth
          variant="standard"
        />
        <br />
        <br />
        <TextField
        id="standard-multiline-flexible"
        label="Link"
        value={link}
        onChange={(e)=>{
          setLink(e.target.value);
        }}
        multiline
        maxRows={1}
        fullWidth
        variant="standard"
      />
      <br />
      <br />
      <TextField
        id="standard-multiline-flexible"
        label="Description"
        value={description}
        onChange={(e)=>{
          setDescription(e.target.value);
        }}
        multiline
        maxRows={4}
        fullWidth
        variant="standard"
      />


        <Button variant="contained" color="success" onClick={()=>{
          console.log("submit buttons hits");
          userService.postCourse({courseName,instructorName,description,link}).then((res)=>{
            console.log(res);
            toast.success("updated Successfully", {
            position: toast.POSITION.TOP_CENTER});
            setChangeHandler(Math.floor(Math.random() * 1111111111111111));
            setModelHandle(false);
          }).catch((err)=>{
            if(err.response.status == 400){
            toast.error(err.response.data, {
              position: toast.POSITION.TOP_CENTER
            });
           }
          })  
        }}  style={{backgroundColor:"#2d5b67"}}>Submit</Button>
        <Button variant="contained" onClick={handleClose} >Close</Button>
        </Box>
      </Modal>  


      </div>
        </>
     );
}
 
export default AddNewEditModel;
