import * as React from 'react';
import Box from '@mui/material/Box';
import Modal from '@mui/material/Modal';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import userService from '../../services/UserService';
import { useState } from 'react';

const EditModel = ({ modelHandle , setModelHandle , course }) => {

    const courseId = useState(course._id)
    const [courseName,setCourseName] =React.useState(course.courseName); 
    const [instructorName,setInstructorName] =React.useState(course.instructorName); 
    const [link,setLink] =React.useState(course.link); 
    const [description,setDescription] =React.useState(course.description); 

    obj ={
      courseName : courseName,
      


    }


    const style = {
        position: 'absolute',
        top: '50%',
        left: '50%',
        transform: 'translate(-50%, -50%)',
        width: "500px",
        height:"420px" ,
        bgcolor: 'background.paper',
        border: '2px solid #000',
        pt: 2,
        px: 4,
        pb: 3,
      };

        const handleClose = () => {
          setModelHandle(false);
        };
      
    return ( 
      <>

      {console.log(courseId)}
      <Modal
        hideBackdrop
        open={modelHandle}
        onClose={handleClose}
        aria-labelledby="child-modal-title"
        aria-describedby="child-modal-description"
      >
        <Box sx={{ ...style}}>

        <h3>Edit Form</h3>
        <TextField
          id="standard-multiline-flexible"
          label="InstructorName"
          value={instructorName}
          onChange={(e)=>{
            setInstructorName(e.target.value);
          }}
          multiline
          maxRows={1}
          fullWidth
          variant="standard"
        />
        <br />
        <br />
        <TextField
          id="standard-multiline-flexible"
          label="CourseTitle"
          value={courseName}
          onChange={(e)=>{
            setCourseName(e.target.value);
          }}
          multiline
          maxRows={1}
          fullWidth
          variant="standard"
        />
        <br />
        <br />
        <TextField
        id="standard-multiline-flexible"
        label="Link"
        value={link}
        onChange={(e)=>{
          setLink(e.target.value);
        }}
        multiline
        maxRows={1}
        fullWidth
        variant="standard"
      />
      <br />
      <br />
      <TextField
        id="standard-multiline-flexible"
        label="Description"
        value={description}
        onChange={(e)=>{
          setDescription(e.target.value);
        }}
        multiline
        maxRows={4}
        fullWidth
        variant="standard"
      />


        <Button variant="contained" color="success" onClick={()=>{
          console.log("submit buttons hits");
          userService.updateCourse(courseId).then((res)=>{
            console.log(res.data);
          }).catch((err)=>{
            console.log(err.message);
          })
        }} >Submit</Button>
        <Button variant="contained" onClick={handleClose} >Close</Button>
        </Box>
      </Modal>  

        </>
     );
}
 
export default EditModel;
