import axios from "axios";
axios.defaults.baseURL = "http://localhost:8080/user"
class GenericServices {
  constructor() {}

  get = (url) => {
    return new Promise((resolve, reject) => {
      axios
        .get(url)
        .then((res) => {
          resolve(res.data);
        })
        .catch((err) => {
          reject(err);
        });
    });
  };

post = (url, data ) => {
    return new Promise((resolve, reject) => {
      axios
        .post(url,data)
        .then((res) => {
          resolve(res.data);
        })
        .catch((err) => {
          reject(err);
        });
    });
  };


  put = (url, data)=>{
      return new Promise((resolve,reject)=>{
        axios.put(url,data).then()


      })

  }



}
export default GenericServices;
