import React from 'react';
import Accordion from "@mui/material/Accordion";
import AccordionDetails from "@mui/material/AccordionDetails";
import AccordionSummary from "@mui/material/AccordionSummary";
import Typography from "@mui/material/Typography";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import { Button , Box, Modal} from "@mui/material";
import EditModel from './EditModel';

export default function SingleProduct({ props, key }) {
  const [open, setOpen] = React.useState(true);
  const [expanded, setExpanded] = React.useState(false);

  const handleChange = (panel) => (event, isExpanded) => {
    setExpanded(isExpanded ? panel : false);
  };



    const ActionButtonStyle ={
      actionBtn:{
        display:"flex",
        justifyContent:"center",
        alignItems:"center",
        marginLetf:"100px"
      }
    }


  return (
    <div>
      <Accordion
        expanded={expanded === "panel1"}
        onChange={handleChange("panel1")}
      >
        <AccordionSummary>
          <Typography sx={{ width: "33%", flexShrink: 0 }}>
            InstructorName
          </Typography>
          <Typography sx={{ color: "text.secondary" }}>
            {props.instructorName}
          </Typography>
        </AccordionSummary>
      </Accordion>
      <Accordion
        expanded={expanded === "panel2"}
        onChange={handleChange("panel2")}
      >
        <AccordionSummary>
          <Typography sx={{ width: "33%", flexShrink: 0 }}>
            Course Title
          </Typography>
          <Typography sx={{ color: "text.secondary" }}>
            {props.courseName}
          </Typography>
        </AccordionSummary>
      </Accordion>
      <Accordion
        expanded={expanded === "panel4"}
        onChange={handleChange("panel4")}
      >
        <AccordionSummary
          expandIcon={<ExpandMoreIcon />}
          aria-controls="panel2bh-content"
          id="panel2bh-header"
        >
          <Typography sx={{ width: "33%", flexShrink: 0 }}>Link</Typography>
          <Typography sx={{ color: "text.secondary" }}>Below</Typography>
        </AccordionSummary>
        <AccordionDetails>
          <Typography>
            <a href={props.link} target="_blank">
              {props.link}
            </a>
          </Typography>
        </AccordionDetails>
      </Accordion>
      <Accordion
        expanded={expanded === "panel3"}
        onChange={handleChange("panel3")}
      >
        <AccordionSummary
          expandIcon={<ExpandMoreIcon />}
          aria-controls="panel3bh-content"
          id="panel3bh-header"
        >
          <Typography sx={{ width: "33%", flexShrink: 0 }}>
            Lecture Description
          </Typography>
        </AccordionSummary>
        <AccordionDetails>
          <Typography>{props.description}</Typography>
        </AccordionDetails>
      </Accordion>
      <div className={ActionButtonStyle.actionBtn}>

      <Button variant="contained" color="success" onClick={()=>{
        console.log("Events Hits");
        <EditModel />
      }}>Edit </Button>
      <Button variant="contained" color="error">Delete </Button>    
      </div>




    </div>
  );
}
